import React, { useState, useEffect } from "react";
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import ProgressBar from "./Meter";
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  const [skillsData, setSkillsData] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:8000/experience/")
      .then((response) => {
        setSkillsData(response.data[0]);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  const [skillsItem, setSkillsItem] = useState([]);

  useEffect(() => {
  axios
    .get("http://localhost:8000/experience-item/")
    .then((response) => {
      setSkillsItem(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
}, []);


  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>{skillsData.title}</h2>
              <p>{skillsData.description}</p>
              <Carousel
                responsive={responsive}
                infinite={true}
                className="owl-carousel owl-theme skill-slider"
              >
                {skillsItem.map((skillsItem) => (
                <div className="item">
                  <ProgressBar percentage={skillsItem.level} text={skillsItem.title} />
                  </div>
                  ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
      <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  );
};
